import React, { Component } from 'react'
import './App.css'
import Card from './Card.js'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      unused: [],
      used: []
    }

    this.draw = this.draw.bind(this)
    this.shuffle = this.shuffle.bind(this)
    this.reset = this.reset.bind(this)
  }

  componentDidMount() {
    this.reset()
  }

  draw() {
    let topCard = this.state.unused
    let used = this.used
    let unused = this.unused


    this.used.unshift(this.unused.shift())
  }

  shuffle() {
    let i = 0
    let j = 0
    let temp = null

    for (i = this.unused.length - 1; i > 0; i--) {
      j = Math.floor(Math.random() * (i + 1))
      temp = this.unused[i]
      this.unused[i] = this.unused[j]
      this.unused[j] = temp
    }
  }

  reset() {
    this.unused = [...Array(53).keys()]
    this.unused.shift()
    this.used = []
  }

  render() {
    return (
      <div className="App">
        <h4>Random Card Generator</h4>
        <div className="controls">
          <button onClick={this.draw}>Draw</button>
          <button onClick={this.shuffle}>Shuffle</button>
          <button onClick={this.reset}>Reset</button>
        </div>
        <Card num = {this.state.used[0]}></Card>
      </div>
    );
  }
}

export default App;
