import React, { Component } from 'react';
import './App.css';
import Card from './Card.js'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      unused: [],
      used: []
    };
  }

  componentDidMount() {
    this.unused = [...Array(53).keys()]
    this.unused.shift()
  }

  render() {
    return (
      <div className="App">
        <h4>Random Card Generator</h4>
        <div className="controls">
          <button onClick="draw">Draw</button>
          <button onClick="reset">Shuffle and reset</button>
        </div>
        <Card name = 'none'></Card>
      </div>
    );
  }
}

export default App;
